SELECT order_num, user_id, order_name, order_zipcode, order_addr, order_deaddr, order_pho, order_price, order_req, order_redate FROM ( SELECT /*+ INDEX_DESC(order_num_tbl SEQ_ORDER_NUM) */ ROWNUM AS RN, order_num, user_id, order_name, order_zipcode, order_addr, order_deaddr, order_pho, order_price, order_req, order_redate FROM order_num_tbl WHERE
ROWNUM <= (#{pageNum} * #{amount})
) WHERE RN > (#{pageNum} - 1) * #{amount}
