mapper.select:
SELECT pro_num, cat_code, pro_name, pro_price, pro_publisher, pro_content, pro_up_folder, pro_img, pro_amount, pro_buy, pro_date, pro_updatedate FROM ( SELECT /*+ INDEX_DESC(product_tbl SEQ_PRO_NUM) */ ROWNUM AS RN, pro_num, cat_code, pro_name, pro_price, pro_publisher, pro_content, pro_up_folder, pro_img, pro_amount, pro_buy, pro_date, pro_updatedate FROM product_tbl WHERE
ROWNUM <= (#{pageNum} * #{amount})
) WHERE RN > (#{pageNum} - 1) * #{amount}

update.foreach:
UPDATE
	product_tbl
SET
	pro_price = #{box.pro_price},pro_buy = #{box.pro_buy}
WHERE
	pro_num = #{box.pro_num}
