mapper.select:
SELECT
	user_email
FROM
	user_tbl
WHERE
	user_id = #{user_id}
AND
	user_name = #{user_name}
AND
	user_email = #{user_email}

mapper.update:
UPDATE
	user_tbl
SET
	user_pwd = #{temp_enc_pw}
WHERE
	user_id = #{user_id}


UPDATE
	user_tbl
SET
	user_email = #{user_email}, user_zipcode = #{user_zipcode}, user_addr = #{user_addr}, user_deaddr = #{user_deaddr}, user_phone = #{user_phone}
WHERE
	user_id = #{user_id}

UPDATE
 	user_tbl
SET
	user_pwd = #{new_user_pwd}
WHERE
	user_id = #{user_id}

mapper.select:
SELECT qna_idx, pro_num, user_id, que_title, que_content, ans_title, ans_content, anscheck, pro_up_folder, pro_img, question_date, answer_date FROM ( SELECT /*+ INDEX_DESC(qnaboard_tbl PK_QNA_IDX) */ ROWNUM AS RN, qna_idx, pro_num, user_id, que_title, que_content, ans_title, ans_content, anscheck, pro_up_folder, pro_img, question_date, answer_date FROM qnaboard_tbl WHERE user_id = #{user_id} AND
ROWNUM <= (#{cri.pageNum} * #{cri.amount}) -- 페이지번호 * 페이지별 출력건수
) WHERE RN > (#{cri.pageNum} - 1 ) * #{cri.amount}
